import React, { useState } from 'react';
import {Image, Video, Transformation, CloudinaryContext} from 'cloudinary-react';
import Dropzone from 'react-dropzone';


function UploadForm(props) {
    // [image, setImage] = useState('');

    return (
        <CloudinaryContext cloudName="diegolealb">
            <div>
                <Dropzone
                    id="direct-upload-dropzone"
                    disableClick={true}
                    multiple={false}
                    accept="image/*"
                    style={{ position: 'relative' }}
                    onDrop={this.onPhotoSelected.bind(this)}
                >
                    <div id="direct_upload">
                        <h1>New Photo</h1>
                        <h2>
                            Direct upload from the browser with React File
                            Upload
                        </h2>
                        <p>
                            You can also drag and drop an image file into the
                            dashed area.
                        </p>
                        <form>
                            <div class="form_line">
                            <label path="title">Title:</label>
                                <div class="form_controls">
                                    <input
                                        type="text"
                                        ref={titleEl =>
                                            (this.titleEl = titleEl)
                                        }
                                        class="form-control"
                                        placeholder="Title"
                                    />
                                </div>
                            </div>
                        </form>
                    </div>
                </Dropzone>
                {/* <Image publicId="sample" width="720"/> */}
            </div>
        </CloudinaryContext>
    )
}

export default UploadForm;
