import React from 'react';
import axios from 'axios';
import {
    Image,
    Transformation,
    CloudinaryContext
} from 'cloudinary-react';

// Format for api calls is https://<apiKey>:<apiSecret>@api.cloudinary.com/<path>
// const CLOUDINARY_URL="https://684518815981178:28_JNXsgqpczG-2Rl4cCd4oZ1PM@api.cloudinary.com/v1_1/diegolealb/resources/image";
const CLOUDINARY_URL="https://api.cloudinary.com/v1_1/diegolealb/resources/image"

class ImageUploadForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gallery: []
        }
    }

    componentDidMount() {
        fetch('https://api.cloudinary.com/v1_1/diegolealb/resources/image', {
        method: 'get',
        headers: {
            'Authorization': 'Basic ' + btoa("684518815981178:28_JNXsgqpczG-2Rl4cCd4oZ1PM"),
            'Access-Control-Allow-Credentials': 'true',
            'Access-Control-Allow-Methods': 'GET',
            'Access-Control-Allow-Origin': 'http://localhost:3000',
        },
        }).then(res => console.log(res.json()))

        // var xhr = new XMLHttpRequest();
        // xhr.open("GET", CLOUDINARY_URL, true);
        // xhr.setRequestHeader(
        //     "Access-Control-Allow-Origin", "*",
        // )
        // xhr.onload = function (e) {
        // if (xhr.readyState === 4) {
        //         if (xhr.status === 200) {
        //         var json_obj = JSON.parse(xhr.responseText);
        //         this.setState({ gallery: json_obj });
        //     } else {
        //         console.error(xhr.statusText);
        //     }
        // }}.bind(this);
        // console.log("state: ", this.state)
        // xhr.onerror = function (e) {
        //     console.error(xhr.statusText);
        // };
        // xhr.send(null);

        axios.get(CLOUDINARY_URL, { 
            headers: {
                "Access-Control-Allow-Origin": "http://localhost:3000",
                'X-Requested-With': 'XMLHttpRequest'
            }
        }).then(res => {
                console.log('Axios response: ', res);
                this.setState({ gallery: res });
            })
            .catch(err => {
                console.error('Axios call: ',err);
            })
    }


    uploadWidget() {
        return (
            <CloudinaryContext cloudName="diegolealb">
                cloudinary.openUploadWidget({
                }, function (err, result) {
                    console.log('result')
                })
            </CloudinaryContext>
        )
    }

    checkUploadResult = (resultEvent) => {
        if (resultEvent.event === 'success') {
            // console.log(this.props.currentUser.id);
            // this.props.postPhoto({user_id: this.props.currentUser.id,
            // caption: '',
            // url: resultEvent.info.secure_url})
            //   .then(this.props.history.push('/profile'))
        }
        // console.log(resultEvent)
    }

    showWidget = () => {
        let widget = window.cloudinary.createUploadWidget({
            cloudName: 'diegolealb',
            uploadPreset: 'default_preset'
        }, (error, result) => { 
            this.checkUploadResult(result) 
        });
        widget.open();
    }

    render() {

        return (
            <div className="mainContainer">
                <h1>Gallery</h1>
                <div className="upload">
                    <button onClick={this.showWidget}>Add Image</button>
                </div>
                <CloudinaryContext cloudName="diegolealb">
                    {
                        this.state.gallery.map(data => {
                            return (
                                <div>
                                {console.log('data', data)}
                                    <div className="img">
                                        <Image publicId={"asdf"}>
                                            <Transformation />
                                        </Image>
                                    </div>
                                </div>
                            )
                        })
                    }
                </CloudinaryContext>
            </div>
        )
    }
}

export default ImageUploadForm;
